import React, { Component } from 'react'; 
import axios from 'axios';

class CounterClick extends Component {

state = {
    counter: '0', 
} 


constructor(props) {
    super(props);

    this.state = {
        counter: '0', 
    };
}

componentDidMount(){
this.increaseCounter();
}

 increaseCounter() {
    fetch('/api/increase/')
    .then(res => { 
            this.setState({
                counter: res.data.response_text,
            });
    })
}

render() {
    return (
    <div> <h2>Click counter</h2>
        <button onClick={this.increaseCounter}>Click me</button>
    <p>Clicks so far {this.state.counter}</p>
        </div>
    );
}

}

export default CounterClick;
