import React from 'react'; 
import axios from 'axios';

const CounterClick = () => {

function increaseCounter() {

    axios.post(`http://localhost/api/increase`, { })
    .then(res => {
      console.log(res);
      console.log(res.data);
    })
}

return (
   <div> <h2>Click counter</h2>
    <button onClick={increaseCounter}>Click me</button>
    <p>Clicks so far 800</p>
    </div>
);
};

export default CounterClick;
