import React from 'react'; 
import axios from 'axios';

const CounterClick = () => {
let counter = 0; 
function increaseCounter() {

    axios.get('/api/increase/')
    .then(res => { 
        this.counter = res.data.response_text;
      console.log(res.data.response_text);
    })
}

return (
   <div> <h2>Click counter</h2>
    <button onClick={increaseCounter}>Click me</button>
<p>Clicks so far {this.counter}</p>
    </div>
);
};

export default CounterClick;
